#leetcode 424
def characterReplacement(s,k) :
        i=0
        j=0
        count=0
        max_count=0
        hm={}
        while i<len(s) and j<len(s):
            for ch in range(i,j+1):
                if s[ch] not in hm:
                    hm[s[ch]]=1
                else:
                    hm[s[ch]]+=1
            mostfreq=0
            for ch in range(i,j+1):
                if hm.get(s[ch])>mostfreq:
                    mostfreq=hm.get(s[ch])
            if (j-i+1)-mostfreq<=k:  
                count+=1
                if(count>max_count):
                    max_count=count
                j+=1
                hm={}
            else:
                count=1
                i+=1
                j=i+1
                hm={}
        return max_count
a=""
print(characterReplacement(a,100))